import React, { useState } from "react";
import ReactDOM from "react-dom";

import BotMessage from "./components/BotMessage";
import UserMessage from "./components/UserMessage";
import Messages from "./components/Messages";
import Input from "./components/Input";
import API from "./ChatbotAPI";
import Header from "./components/Header";
import chatbotImage1 from '/Users/calvinbeighle/Desktop/Fall2023/LitGPT/LitGPTFrontEnd/ProteinImage.jpeg';

import "./styles.css";

// Example Image Import (if the image is local)
import chatbotImage from '/Users/calvinbeighle/Desktop/Fall2023/LitGPT/LitGPTFrontEnd/ProteinImage.jpeg'; // Adjust the path as needed

function Chatbot() {
  const [messages, setMessages] = useState([]);

  const send = async text => {
    const newMessages = messages.concat(
      <UserMessage key={messages.length + 1} text={text} />,
      <BotMessage
        key={messages.length + 2}
        fetchMessage={async () => await API.GetChatbotResponse(text)}
      />
    );
    setMessages(newMessages);
  };

  return (
    <div className="chatbot">
      <Header />
      <Messages messages={messages} />
      <Input onSend={send} />
      {/* Displaying the Image */}
      <img src={chatbotImage} alt="Chatbot" /> {/* Adjust the alt text as needed */}
    </div>
  );
}

const rootElement = document.getElementById("root");
ReactDOM.render(<Chatbot />, rootElement);
